    public static ArrayList<Rempah> getListData(){
        ArrayList<Rempah> list = new ArrayList<>();
        for (String[] aData : data) {
            Rempah rempah = new Rempah();
            rempah.setNama_rempah(aData[0]);
            rempah.setKeterangan(aData[1]);
            rempah.setPhoto(aData[2]);
            rempah.setDetail(aData[3]);

            list.add(rempah);
        }

        return list;
    }
